[101001]={ id=101001, name="如意筋斗云", sImg="101001", type=1, grade=1, star=0, hp=32, bonusHp=196, bonusAtk=240, bonusMechDef=232, bonusFairyDef=600, bonusDemonDef=464, speed=10, skillID1=211001, skillID2=211004, skillID3=211014, skillID4=221001, killPoint="3-3", bigAtkType=0, mSpineName="spine/flyObject/101001_jindouyun", pos1="63|171", pos2="-64|86", pos3="63|1", pos4="-64|-84", pos5="63|-169", size="287|90", shootPos="91|-21", boombPos="91|-21", desc="白色机械载具，排气管会不断喷出大团白色浓烟雾，机体地盘基本被云雾遮挡。看上去好像飘在云上。孙悟空出师的时候，菩提祖师送他的飞宝。因为用了很久已经有些旧了，但是悟空从来舍不得改造，也没想过换掉他。是悟空最重要的宝贝。"}, 
[101002]={ id=101002, name="黄巾力士", sImg="101002", type=1, grade=1, star=0, hp=37, bonusHp=156, bonusAtk=260, bonusMechDef=234, bonusFairyDef=606, bonusDemonDef=428, speed=15, skillID1=211002, skillID2=211003, skillID3=211004, skillID4=221002, killPoint="3-3", bigAtkType=0, mSpineName="spine/flyObject/101002_huangjinlishi", pos1="63|171", pos2="-64|86", pos3="63|1", pos4="-64|-84", pos5="63|-169", size="260|90", shootPos="76|-9", boombPos="76|-9", desc="很多散仙喜欢的一种飞宝，人形机器人。根据散仙的喜好不同，黄巾力士也有多种外形差距，有的头部可以坐人，有的是骑在背上，还有的骑在肩膀上等等。其实最早的黄巾力士是太上老君还没飞升，作为散仙的时候无聊研制的。没想到意外成了散仙界的流行飞宝。"}, 
[101003]={ id=101003, name="风火轮", sImg="101003", type=1, grade=1, star=0, hp=26, bonusHp=204, bonusAtk=200, bonusMechDef=168, bonusFairyDef=660, bonusDemonDef=480, speed=20, skillID1=211003, skillID2=211004, skillID3=211005, skillID4=221003, killPoint="3-3", bigAtkType=1, mSpineName="spine/flyObject/101003_fenghuolun", pos1="63|171", pos2="-64|86", pos3="63|1", pos4="-64|-84", pos5="63|-169", size="270|67", shootPos="75|5", boombPos="75|5", desc="哪吒的飞宝，是滑板改装的风火轮，有大团三昧真火火焰特效，非常中二和炫酷。行驶速度超快，因为经常超速架势，李靖经常收到天界开给哪吒的罚单。"}, 
[101004]={ id=101004, name="玉琵琶", sImg="101004", type=1, grade=1, star=0, hp=30, bonusHp=166, bonusAtk=182, bonusMechDef=180, bonusFairyDef=510, bonusDemonDef=360, speed=25, skillID1=211001, skillID2=211002, skillID3=211003, skillID4=211002, killPoint="3-3", bigAtkType=0, mSpineName="spine/flyObject/AirShips_Beta_1", pos1="-20|222", pos2="3|81", pos3="-53|-104", pos4="93|166", pos5="94|-73", size="508|495", shootPos="14|49", boombPos="14|49", desc="被改造的几乎看不出本来是白玉制品的琵琶状飞宝，原是蝎子精的飞宝，但是蝎子精被打死后，女儿国王发现了这件宝贝，并且为了她的唐僧哥哥也开始修仙之路。但因为玉琵琶残留的戾气较重，本身性格受了一定影响，产生了一个暴戾的双重人格，时常失控。"}, 
[101005]={ id=101005, name="玉兔车", sImg="101005", type=1, grade=1, star=0, hp=35, bonusHp=240, bonusAtk=202, bonusMechDef=234, bonusFairyDef=528, bonusDemonDef=336, speed=30, skillID1=211001, skillID2=211002, skillID3=211003, skillID4=211002, killPoint="3-3", bigAtkType=0, mSpineName="spine/flyObject/AirShips_Beta_1", pos1="-20|222", pos2="3|81", pos3="-53|-104", pos4="93|166", pos5="94|-73", size="508|495", shootPos="14|49", boombPos="14|49", desc="机械玉兔，嫦娥的飞宝，外形虽然圆滚滚很可爱但是两个耳朵其实是超级夸张的重型武器（类似炮筒）。"}, 
[101006]={ id=101006, name="初号机", sImg="101006", type=1, grade=1, star=0, hp=33, bonusHp=164, bonusAtk=156, bonusMechDef=168, bonusFairyDef=612, bonusDemonDef=452, speed=30, skillID1=211001, skillID2=211002, skillID3=211003, skillID4=221004, killPoint="3-3", bigAtkType=0, mSpineName="spine/flyObject/101006_jijia", pos1="63|171", pos2="-64|86", pos3="63|1", pos4="-64|-84", pos5="63|-169", size="264|224", shootPos="110|-6", boombPos="110|-6", desc="白色小飞机，人界和洞天福地的小怪经常使用的载具。"}, 
[102001]={ id=102001, name="九齿逊金耙", sImg="102001", type=2, grade=1, star=0, hp=35, bonusHp=212, bonusAtk=226, bonusMechDef=352, bonusFairyDef=190, bonusDemonDef=510, speed=10, skillID1=212001, skillID2=212002, skillID3=212003, skillID4=222002, killPoint="3-3", bigAtkType=2, mSpineName="spine/flyObject/102001_jiuchidingpa", pos1="63|171", pos2="-64|86", pos3="63|1", pos4="-64|-84", pos5="63|-169", size="304|88", shootPos="84|-15", boombPos="84|-15", desc="太上老君给猪八戒打造的灵宝，高科技飞宝兵器，本来看外形很像机巧系的飞宝。但是九齿钉耙是神冰铁原料，借五方五帝、六丁六甲之力锻造而成。从选材角度来说依然属于灵宝类。不过喜欢华丽的八戒成佛后又对这个飞宝做了额外改造，增加了很多白玉，宝石的镶嵌。看上去倒是比以前像灵宝系多了。能乘坐的钉耙头部分是一个漏斗装，下方有九根尖刺，后面接着一根柄。"}, 
[102002]={ id=102002, name="七宝琉璃盏", sImg="102002", type=2, grade=1, star=0, hp=33, bonusHp=242, bonusAtk=224, bonusMechDef=424, bonusFairyDef=162, bonusDemonDef=672, speed=15, skillID1=212003, skillID2=212004, skillID3=212005, skillID4=222001, killPoint="3-3", bigAtkType=0, mSpineName="spine/flyObject/102002_liulizhan", pos1="63|171", pos2="-64|86", pos3="63|1", pos4="-64|-84", pos5="63|-169", size="215|87", shootPos="96|-7", boombPos="96|-7", desc="沙悟净的灵宝，巨大的半透明琉璃造飞宝。镶嵌很多珍贵的宝珠。但是飞宝上有很多机械焊接的裂纹。因为曾经被他自己打碎过。成佛后，太上老君帮忙修复了琉璃盏碎片，做成了整个琉璃盏送给了沙僧。"}, 
[102003]={ id=102003, name="宝莲灯", sImg="102003", type=2, grade=1, star=0, hp=25, bonusHp=244, bonusAtk=190, bonusMechDef=332, bonusFairyDef=220, bonusDemonDef=624, speed=20, skillID1=211001, skillID2=211002, skillID3=211003, skillID4=211002, killPoint="3-3", bigAtkType=0, mSpineName="spine/flyObject/AirShips_Beta_1", pos1="-20|222", pos2="3|81", pos3="-53|-104", pos4="93|166", pos5="94|-73", size="508|495", shootPos="14|49", boombPos="14|49", desc="女娲补天后，天火凝日月精华化成的宝灯，沉香的飞宝。造型十分华贵，浑然天成，流光溢彩，仅在莲瓣尖端和莲心有一点机械感。"}, 
[102004]={ id=102004, name="羊脂玉净瓶", sImg="102004", type=2, grade=1, star=0, hp=26, bonusHp=180, bonusAtk=214, bonusMechDef=396, bonusFairyDef=196, bonusDemonDef=546, speed=25, skillID1=211001, skillID2=211002, skillID3=211003, skillID4=211002, killPoint="3-3", bigAtkType=0, mSpineName="spine/flyObject/AirShips_Beta_1", pos1="-20|222", pos2="3|81", pos3="-53|-104", pos4="93|166", pos5="94|-73", size="508|495", shootPos="14|49", boombPos="14|49", desc="老君炼制的后天灵宝，是金角大王和银角大王的飞宝，瓶身上被两人增加了很孩子气的恶趣味装饰和涂鸦花纹。机械感主要在瓶塞上体现。"}, 
[102005]={ id=102005, name="太阴芭蕉扇", sImg="102005", type=2, grade=1, star=0, hp=23, bonusHp=216, bonusAtk=226, bonusMechDef=460, bonusFairyDef=218, bonusDemonDef=702, speed=30, skillID1=212005, skillID2=212006, skillID3=212007, skillID4=222003, killPoint="3-3", bigAtkType=0, mSpineName="spine/flyObject/102005_bajiaoshan", pos1="63|171", pos2="-64|86", pos3="63|1", pos4="-64|-84", pos5="63|-169", size="251|56", shootPos="68|8", boombPos="68|8", desc="昆仑山后自混沌开辟以来，天地产成的一个灵宝，乃太阴之精叶，故能灭火气。本来是太上老君的宝物，不知道什么时候到了铁扇公主手里成了她的飞宝，叶柄，叶脉都有明显的金属感和流光。叶柄的机械结构很复杂。"}, 
[103001]={ id=103001, name="金翅黑点虎", sImg="103001", type=3, grade=1, star=0, hp=24, bonusHp=162, bonusAtk=184, bonusMechDef=534, bonusFairyDef=428, bonusDemonDef=236, speed=10, skillID1=213001, skillID2=213002, skillID3=213006, skillID4=223001, killPoint="3-3", bigAtkType=0, mSpineName="spine/flyObject/103001_laohu", pos1="63|171", pos2="-64|86", pos3="63|1", pos4="-64|-84", pos5="63|-169", size="248|102", shootPos="83|-51", boombPos="83|-51", desc="身上有大片黑色斑点斑纹和金色纹路的活体老虎，老虎的头部，身体，四肢上有机械类护甲，背上一对人工机械制的翅膀，双翼的部分可以站人。"}, 
[103002]={ id=103002, name="白龙马", sImg="103002", type=3, grade=1, star=0, hp=37, bonusHp=238, bonusAtk=186, bonusMechDef=702, bonusFairyDef=368, bonusDemonDef=180, speed=15, skillID1=211001, skillID2=211002, skillID3=211003, skillID4=211002, killPoint="3-3", bigAtkType=0, mSpineName="spine/flyObject/AirShips_Beta_1", pos1="-20|222", pos2="3|81", pos3="-53|-104", pos4="93|166", pos5="94|-73", size="508|495", shootPos="14|49", boombPos="14|49", desc="中式的白龙外形，除了头部其他部分都被机械包裹，腹部露出大量机械结构和肋骨，爪子是全机械。"}, 
[103003]={ id=103003, name="避水金睛兽", sImg="103003", type=3, grade=1, star=0, hp=23, bonusHp=210, bonusAtk=140, bonusMechDef=618, bonusFairyDef=444, bonusDemonDef=160, speed=20, skillID1=211001, skillID2=211002, skillID3=211003, skillID4=211002, killPoint="3-3", bigAtkType=0, mSpineName="spine/flyObject/AirShips_Beta_1", pos1="-20|222", pos2="3|81", pos3="-53|-104", pos4="93|166", pos5="94|-73", size="508|495", shootPos="14|49", boombPos="14|49", desc="龙口、狮头、鱼鳞、牛尾、虎爪、鹿角，全身赤红。是多种生物的融合体。"}, 
[103004]={ id=103004, name="灵感大王", sImg="103004", type=3, grade=1, star=0, hp=26, bonusHp=212, bonusAtk=174, bonusMechDef=492, bonusFairyDef=448, bonusDemonDef=194, speed=25, skillID1=213002, skillID2=211003, skillID3=213005, skillID4=223002, killPoint="3-3", bigAtkType=0, mSpineName="spine/flyObject/103004_linggan", pos1="63|171", pos2="-64|86", pos3="63|1", pos4="-64|-84", pos5="63|-169", size="297|86", shootPos="77|-16", boombPos="77|-16", desc="原本是观音养的金鱼，下界作死以后被收服回去，结果被观音收服后的红孩儿（善财童子）改造成了半机械半鱼的金鱼飞宝。红孩儿没事就要给它加点零件改造一下。从此过着生不如死水深火热的生活。"}, 
[103005]={ id=103005, name="太乙金光鹤", sImg="103005", type=3, grade=1, star=0, hp=22, bonusHp=234, bonusAtk=152, bonusMechDef=666, bonusFairyDef=332, bonusDemonDef=212, speed=30, skillID1=213001, skillID2=213002, skillID3=213007, skillID4=223003, killPoint="3-3", bigAtkType=0, mSpineName="spine/flyObject/103005_jinguanghe", pos1="63|171", pos2="-64|86", pos3="63|1", pos4="-64|-84", pos5="63|-169", size="283|101", shootPos="112|1", boombPos="112|1", desc="太乙真人的坐骑，因为太乙真人个人的恶趣味，身上有不少金色发光的机械装饰，是少有的几乎没有多少机械结构的神兽。因为太乙真人认为太多机械结构，仙鹤就看起来不够仙，不帅了。"}, 
